        <link rel="stylesheet" href="<?php echo get_template_directory_uri(); ?>/assets/scss/not-gulp/1. header.css" as="style">

        <script>
            document.addEventListener( 'DOMContentLoaded', function () {
                let header = document.querySelector('.main-header'),
                    menu = document.querySelector('.main-menu'),
                    directionsClick = menu.querySelector('.directions'),
                    coachesClick = menu.querySelector('.coaches'),
                    hamburger = document.querySelector('.main-hamburger'),
                    openMenus = document.querySelectorAll('.openMenu'),
                    closedMenu = menu.querySelector('.body__hamburger .hamburger__hamburger');

                //ЛОГИКА РАСПОЛОЖЕНИЯ ФИКСИРОВАННОГО БУРГЕРА
                function alignHamburgerToContainer() {
                    const container = document.querySelector('.container');

                    const containerRect = container.getBoundingClientRect();
                    const containerRight = containerRect.right;
                    const viewportRight = window.innerWidth;

                    const rightOffset = viewportRight - containerRight;

                    hamburger.style.right = `${rightOffset}px`;
                }

                window.addEventListener('load', alignHamburgerToContainer);
                window.addEventListener('resize', alignHamburgerToContainer);

                //ЛОГИКА ПОЯВЛЕНИЯ ФИКСИРОВАННОГО БУРГЕРА
                const observer = new IntersectionObserver(
                    (entries) => {
                        const isHeaderVisible = entries[0].isIntersecting;

                        if (isHeaderVisible) {
                            hamburger.classList.remove('opacity');
                        } else {
                            hamburger.classList.add('opacity');
                        }
                    },
                    {
                        root: null,
                        threshold: 0.1,
                    }
                );

                observer.observe(header);

                //ЛОГИКА РАБОТЫ BURGER MENU
                openMenus.forEach(openMenu => {
                    openMenu.onclick = function (e) {
                        e.preventDefault();

                        openMenus.forEach(openMenu => {
                            openMenu.classList.toggle('active');
                        });
                        closedMenu.classList.toggle('active');

                        document.documentElement.classList.toggle('no-scroll');

                        if (document.documentElement.classList.contains('no-scroll')) {
                            document.documentElement.style.marginRight = window.scrollWidth + 'px';

                            openMenus.forEach(openMenu => {
                                if (openMenu.classList.contains('withoutHeader')) {
                                    const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                    openMenu.closest('.main-hamburger').style.right = (currentRight + window.scrollWidth) + 'px';
                                }
                            });
                        } else {
                            document.documentElement.style.removeProperty('margin-right');

                            openMenus.forEach(openMenu => {
                                if (openMenu.classList.contains('withoutHeader')) {
                                    const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                    openMenu.closest('.main-hamburger').style.right = (currentRight - window.scrollWidth) + 'px';
                                }
                            });
                        }

                        header.classList.toggle('open-menu');
                        menu.classList.toggle('open-menu');
                    };
                });

                closedMenu.onclick = function (e) {
                    e.preventDefault();

                    openMenus.forEach(openMenu => {
                        openMenu.classList.toggle('active');
                    });
                    closedMenu.classList.toggle('active');

                    document.documentElement.classList.toggle('no-scroll');

                    if (document.documentElement.classList.contains('no-scroll')) {
                        document.documentElement.style.marginRight = window.scrollWidth + 'px';

                        openMenus.forEach(openMenu => {
                            if (openMenu.classList.contains('withoutHeader')) {
                                const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                openMenu.closest('.main-hamburger').style.right = (currentRight + window.scrollWidth) + 'px';
                            }
                        });
                    } else {
                        document.documentElement.style.removeProperty('margin-right');

                        openMenus.forEach(openMenu => {
                            if (openMenu.classList.contains('withoutHeader')) {
                                const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                openMenu.closest('.main-hamburger').style.right = (currentRight - window.scrollWidth) + 'px';
                            }
                        });
                    }

                    header.classList.toggle('open-menu');
                    menu.classList.toggle('open-menu');
                };

                directionsClick.onclick = function (e) {
                    openMenus.forEach(openMenu => {
                        openMenu.classList.toggle('active');
                    });
                    closedMenu.classList.toggle('active');

                    document.documentElement.classList.toggle('no-scroll');

                    if (document.documentElement.classList.contains('no-scroll')) {
                        document.documentElement.style.marginRight = window.scrollWidth + 'px';

                        openMenus.forEach(openMenu => {
                            if (openMenu.classList.contains('withoutHeader')) {
                                const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                openMenu.closest('.main-hamburger').style.right = (currentRight + window.scrollWidth) + 'px';
                            }
                        });
                    } else {
                        document.documentElement.style.removeProperty('margin-right');

                        openMenus.forEach(openMenu => {
                            if (openMenu.classList.contains('withoutHeader')) {
                                const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                openMenu.closest('.main-hamburger').style.right = (currentRight - window.scrollWidth) + 'px';
                            }
                        });
                    }

                    header.classList.toggle('open-menu');
                    menu.classList.toggle('open-menu');
                };

                coachesClick.onclick = function (e) {
                    openMenus.forEach(openMenu => {
                        openMenu.classList.toggle('active');
                    });
                    closedMenu.classList.toggle('active');

                    document.documentElement.classList.toggle('no-scroll');

                    if (document.documentElement.classList.contains('no-scroll')) {
                        document.documentElement.style.marginRight = window.scrollWidth + 'px';

                        openMenus.forEach(openMenu => {
                            if (openMenu.classList.contains('withoutHeader')) {
                                const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                openMenu.closest('.main-hamburger').style.right = (currentRight + window.scrollWidth) + 'px';
                            }
                        });
                    } else {
                        document.documentElement.style.removeProperty('margin-right');

                        openMenus.forEach(openMenu => {
                            if (openMenu.classList.contains('withoutHeader')) {
                                const currentRight = parseInt(getComputedStyle(openMenu.closest('.main-hamburger')).right) || 0;
                                openMenu.closest('.main-hamburger').style.right = (currentRight - window.scrollWidth) + 'px';
                            }
                        });
                    }

                    header.classList.toggle('open-menu');
                    menu.classList.toggle('open-menu');
                };

                const lines = [
                    document.querySelector('.lines-line.line-1'),
                    document.querySelector('.lines-line.line-2'),
                    document.querySelector('.lines-line.line-4'),
                    document.querySelector('.lines-line.line-6'),
                    document.querySelector('.lines-line.line-8')
                ];

                const screenWidth = window.innerWidth;

                // Функция для генерации случайной позиции right
                function getRandomPosition() {
                    return Math.floor(Math.random() * (screenWidth - 200)) + "px"; // 200 - это максимальная ширина элемента
                }

                // Функция для генерации случайного расстояния с минимум 40px
                function getRandomDistance() {
                    const maxDistance = screenWidth / 2; // Максимальное расстояние до половины ширины экрана
                    return Math.floor(Math.random() * (maxDistance - 200)) + 200; // Минимальное значение 40px
                }

                // Добавляем переменную для хранения следующего времени обновления
                let nextFrameTime = 0;

                function animateLines() {
                    const now = performance.now();
                    if (now < nextFrameTime) {
                        requestAnimationFrame(animateLines); // Ждем следующего кадра
                        return;
                    }

                    nextFrameTime = now + 1000; // Устанавливаем время следующего обновления (1 секунда)

                    lines.forEach(line => {
                        let currentRight = parseFloat(getComputedStyle(line).right);
                        const randomDistance = getRandomDistance(); // Используем новую функцию для получения расстояния
                        const direction = Math.random() < 0.5 ? -1 : 1; // Случайное направление (влево или вправо)
                        const newRight = currentRight + (direction * randomDistance);

                        // Получаем ширину линии для проверки границ
                        const lineWidth = line.offsetWidth;

                        // Проверяем, что новое значение right не выходит за границы экрана
                        if (newRight < 0) {
                            line.style.right = "0px"; // Если выходит за пределы, устанавливаем в 0
                        } else if (newRight > screenWidth - lineWidth) { // Учитываем ширину элемента
                            line.style.right = `${screenWidth - lineWidth}px`; // Устанавливаем максимальное значение right
                        } else {
                            line.style.right = `${newRight}px`;
                        }
                    });

                    // Запускаем следующую анимацию
                    requestAnimationFrame(animateLines);
                }

                // Инициализация: устанавливаем случайное значение right для каждого элемента
                lines.forEach(line => {
                    line.style.right = getRandomPosition(); // Установка начального значения right
                });

                // Запускаем анимацию
                animateLines();
            });
        </script>

