    <link rel="stylesheet" href="<?php echo get_template_directory_uri(); ?>/assets/scss/not-gulp/default-slider.css" as="style">

    <script>
        document.addEventListener( 'DOMContentLoaded', function () {
            let sliderSection = document.querySelector('.default-sliders__slider'),
                sliderContainer = sliderSection.querySelector('.container-slider');

            //РАСЧЕТ СТИЛЕЙ ДЛЯ СЛАЙДЕРА
            let sliderBody = sliderSection.querySelector('.slider__body'),
                sliderMain = sliderSection.querySelector('.default-main'),
                sliderMore = sliderSection.querySelector('.default-more'),
                offsetSecondSlider;

            if (window.outerWidth <= 1024) {
                offsetSecondSlider = 15;
            } else if (window.outerWidth <= 1280) {
                offsetSecondSlider = 50;
            } else if (window.outerWidth <= 1600) {
                offsetSecondSlider = 75;
            } else {
                offsetSecondSlider = 100;
            }

            sliderMore.style.right = (-((sliderSection.getBoundingClientRect().width - sliderMore.getBoundingClientRect().right) - (sliderBody.getBoundingClientRect().width - sliderMore.getBoundingClientRect().width))) - sliderMore.getBoundingClientRect().width + offsetSecondSlider + 'px';

            //РАСЧЕТ СТИЛЕЙ ДЛЯ ЛИНИЙ
            let topLine = sliderSection.querySelector('.body__top-line'),
                bottomLeftLine = sliderSection.querySelector('.body__bottom-left-line'),
                bottomRightLine = sliderSection.querySelector('.body__bottom-right-line'),
                bottomArrowLine = sliderSection.querySelector('.body__bottom-arrow'),
                textContainer = sliderSection.querySelector('.body__info'),
                iconsBeforeText = sliderSection.querySelector('.info__icons'),
                paddingOffsetTopContainer,
                paddingOffsetBottomContainer,
                topOffsetBottomLine,
                betweenOffsetBottomLine;

            if (window.outerWidth <= 1024) {
                paddingOffsetTopContainer = 70;
                paddingOffsetBottomContainer = 40;
                topOffsetBottomLine = 20;
                betweenOffsetBottomLine = 40;
            } else if (window.outerWidth <= 1280) {
                paddingOffsetTopContainer = 110;
                paddingOffsetBottomContainer = 80;
                topOffsetBottomLine = 30;
                betweenOffsetBottomLine = 95;
            } else if (window.outerWidth <= 1600) {
                paddingOffsetTopContainer = 150;
                paddingOffsetBottomContainer = 120;
                topOffsetBottomLine = 35;
                betweenOffsetBottomLine = 95;
            } else {
                paddingOffsetTopContainer = 185;
                paddingOffsetBottomContainer = 155;
                topOffsetBottomLine = 40;
                betweenOffsetBottomLine = 165;
            }

            if (window.outerWidth <= 1024) {
                topLine.style.left = -(sliderContainer.getBoundingClientRect().left - 10) + 'px';
                topLine.style.right = -(sliderSection.getBoundingClientRect().width - sliderContainer.getBoundingClientRect().right - 10) + 'px';
            } else {
                topLine.style.left = -(sliderContainer.getBoundingClientRect().left) + 'px';
                topLine.style.right = -((sliderSection.getBoundingClientRect().width - sliderContainer.getBoundingClientRect().right) + (sliderBody.getBoundingClientRect().width - iconsBeforeText.getBoundingClientRect().right - iconsBeforeText.getBoundingClientRect().width - 35)) + 'px';
            }

            bottomLeftLine.style.top = -paddingOffsetTopContainer + (paddingOffsetTopContainer + textContainer.getBoundingClientRect().height + paddingOffsetBottomContainer + topOffsetBottomLine)  + 'px';
            bottomLeftLine.style.left = -(sliderContainer.getBoundingClientRect().left) + 'px';
            bottomRightLine.style.top = -paddingOffsetTopContainer + (paddingOffsetTopContainer + textContainer.getBoundingClientRect().height + paddingOffsetBottomContainer + topOffsetBottomLine)  + 'px';
            bottomRightLine.style.left = -(sliderContainer.getBoundingClientRect().left - bottomLeftLine.getBoundingClientRect().width - betweenOffsetBottomLine)  + 'px';
            bottomArrowLine.style.top = -paddingOffsetTopContainer + (paddingOffsetTopContainer + textContainer.getBoundingClientRect().height + paddingOffsetBottomContainer + topOffsetBottomLine - (bottomArrowLine.getBoundingClientRect().height/2))  + 'px';
            bottomArrowLine.style.left = -(sliderContainer.getBoundingClientRect().left - bottomRightLine.getBoundingClientRect().right - 10) + 'px';
        });
    </script>
</section>